// import logo from './logo.svg';
// import './App.css';
import React from "react";

const todos = [
  {text:'Curso React V1', completed: false},
  {text:'Curso React V2', completed: false},
  {text:'Curso React V3', completed: false}
];

function App() {
  return (
    <React.Fragment>
      <TodoCounter />
      <h2>Has completado 2 de 3 TODOs</h2>

      <TodoSearch/>
      <input placeholder="Buscar..." />

      <TodoList>
        {/* <TodoItem /> */}
        {todos.map(todo => (
          <TodoItem />
        ))}
      </TodoList>
      <CreateTodoButton />
      <button>+</button>
    </React.Fragment>
  );
}

export default App;
